az policy definition create --name "Activity logs (Administrative) should be exported for retention" --mode All --description "Audit enabling of resource logs. This enables you to recreate activity trails to use for investigation purposes when a security incident occurs or when your network is compromised." --metadata "category=NewCompliance" --rules .\rules-admin.json --params .\params.json
