az policy definition create --name "Resource logs (Read) in Storage Account should be enabled" --mode All --description "Audit enabling of resource logs. This enables you to recreate activity trails to use for investigation purposes when a security incident occurs or when your network is compromised." --metadata "category=NewCompliance" --rules .\rules-read.json --params .\params.json
az policy definition create --name "Resource logs (Write) in Storage Account should be enabled" --mode All --description "Audit enabling of resource logs. This enables you to recreate activity trails to use for investigation purposes when a security incident occurs or when your network is compromised." --metadata "category=NewCompliance" --rules .\rules-write.json --params .\params.json
az policy definition create --name "Resource logs (Delete) in Storage Account should be enabled" --mode All --description "Audit enabling of resource logs. This enables you to recreate activity trails to use for investigation purposes when a security incident occurs or when your network is compromised." --metadata "category=NewCompliance" --rules .\rules-delete.json --params .\params.json
